<script>
    $(document).ready(function () {
        $('.carousel[data-type="multi"] .item').each(function () {
            var next = $(this).next();
            console.log(next);
            if (!next.length) {
                next = $(this).siblings(':first');
            }
            next.children(':first-child').clone().appendTo($(this));

            for (var i = 0; i < 2; i++) {
                next = next.next();
                if (!next.length) {
                    next = $(this).siblings(':first');
                }

                next.children(':first-child').clone().appendTo($(this));
            }
        });

    });
</script>
